import { useState } from "react";
import { useViewport } from "../../hook";
import Modal from "../Modal/Modal";
import "./Content.scss";
import ContentItem from "./ContentItem";

function Content() {
  const [isModal, setIsModal] = useState(false);
  const [width] = useViewport()
  function handleModal() {
    setIsModal(!isModal);
  }
  return (
    <div className="content" style={width > 1200 ? {gridTemplateColumns: 'repeat(5,auto)'} : 
    width > 1000 ? {gridTemplateColumns: 'repeat(4,auto)'} : width > 760 ? {gridTemplateColumns: 'repeat(3,auto)'} 
    : width > 600 ? {gridTemplateColumns: 'repeat(2,auto)'} : {gridTemplateColumns: 'repeat(4,auto)'}}>
      <ContentItem onClick={handleModal} />
      <Modal show={isModal} onClick={handleModal} />
    </div>
  );
}

export default Content;
