import { useState } from "react";
import "./Content.scss";
import * as type from "../../store/action";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
function ContentItem({ onClick, data }) {
  const dispath = useDispatch();
  const {Movie,MovieDetail} = useSelector((state) => state.movie)

  useEffect(() => {
    dispath(type.getMovieDetail(data));
  },[])

  // function getDetailMovie(data) {
  //   dispath(type.getMovieDetail(data));
  // }
  return (
    <div
      className="content_item"
      onClick={function () {
        onClick();
      }}
    >
      <img className="content_img" src={data.imageUrl} alt="hinh anh" />
      <p className="content_name">{data.title}</p>
    </div>
  );
}

export default ContentItem;
