import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import * as type from "../../store/action";
import "./Content.scss";
function ContentItem({movieUrl ,getInfo, onClick, data }) {
  const dispath = useDispatch();
  const [movie, setMovie] = useState({});
  const [urlMovie,setUrlMovie] = useState({})
  useEffect(() => {
    type.request
      .get(`/phim/${data.slug}`)
      .then((res) => {
          setMovie(res.data.movie)
          setUrlMovie(res.data.episodes[0].server_data)
      });
  }, []);

  return (
    <div
      className="content_item"
      onClick={function () {
        getInfo(movie);
        movieUrl(urlMovie)
        onClick();
      }}
    >
      <img
        className="content_img"
        src={movie && movie.poster_url}
        alt="hinh anh"
      />
      <p className="content_name">{movie && movie.name}</p>
    </div>
  );
}

export default ContentItem;
