import "./Search.scss";
import SearchItem from "./SearchItem";
import { useViewport } from "../../../hook";
import { useLocation } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {AiOutlineLoading} from "react-icons/ai"
import * as types from "../../../store/action";

const useQuery = () => new URLSearchParams(useLocation().search);

function Search() {
    const [loading,setLoading] = use
  const dispath = useDispatch();
  const keywords = useQuery().get("keywords");
  const Search = keywords
    .normalize("NFD")
    .replace(/ /g, "-")
    .replace(/[\u0300-\u036f]/g, "")
    .replace(/đ/g, "d")
    .replace(/Đ/g, "D");

  const [width] = useViewport();
  const { SearchMovie } = useSelector((state) => state.movie);
  useEffect(() => {
    const handler = setTimeout(() => {
      dispath(types.searchMovie(Search.trim()));
    }, 800);

    return () => {
      clearTimeout(handler);
    };
  }, [Search]);
  console.log(Search);
  console.log(SearchMovie);
  return (
    <div
      className="search"
      style={
        width > 1200
          ? { gridTemplateColumns: "repeat(5,auto)" }
          : width > 1000
          ? { gridTemplateColumns: "repeat(4,auto)" }
          : width > 760
          ? { gridTemplateColumns: "repeat(3,auto)" }
          : width > 600
          ? { gridTemplateColumns: "repeat(2,auto)" }
          : { gridTemplateColumns: "repeat(1,auto)" }
      }
    >
      <SearchItem />
      {loading && (
        <div className="content_loading">
          <AiOutlineLoading className="content_loading-icon" />
        </div>
      )}
    </div>
  );
}

export default Search;
