import axios from "axios";
import * as types from "../type";

const request = axios.create({
  baseURL: "https://ophim1.com/danh-sach/",
  headers: {
    "content-type": "aplication/json"
  }
})

export const getMoviePage = () => async (dispath) => {
  try {
    const result = await request.get(
      `https://proxy.votuan.xyz/?url=https%3A%2F%2Fophim1.com%2Fdanh-sach%2Fphim-moi-cap-nhat%3Fname%3Dtuan%26age%3D12&method=get&fbclid=IwAR1_4Mh62n-fil_JCCNbj6IOXFAK83nvG3HWx103_YyCCsLCYXs6w3UI750`
    );
    dispath({
      type: types.GET_MOVIE_PAGE,
      payload: result.data,
    });
  } catch (error) {
    console.log("Get movie API error", error);
  }
};

export const getMovieDetail = (movie) => function(dispath) {
  return dispath({ type: types.GET_MOVIE_DETAIL, payload: movie });
}
