    function handleModal() {
        setIsModal(!isModal)
    }
    return ( 
        <themeContext.Provider value={is}>
            <div className="content_item" onClick={handleModal}>
                <img className="content_img" src="https://tvseriesfinale.com/wp-content/uploads/2020/12/sealteam04.jpg" alt="hinh anh" />
                <p className="content_name">Navy SEAL team</p>
            </div>
        </themeContext.Provider>
     );
}

