import axios from "axios";
import * as types from "../type";

export const getMoviePage = () => async (dispath) => {
  try {
    const result = await axios.get(
      `https://api.themoviedb.org/3/discover/movie?api_key=342cb7f1adeb46b695407d2f5b6ad93e&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate`
    );
    dispath({
      type: types.GET_MOVIE_PAGE,
      payload: result.data,
    });
  } catch (error) {
    console.log("Get movie API error", error);
  }
};

export const getMovieDetail = (movie) => function(dispath) {
  return dispath({ type: types.GET_MOVIE_DETAIL, payload: movie });
}
