import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useViewport } from "../../hook";
import * as type from "../../store/action";
import Modal from "../Modal/Modal";
import "./Content.scss";
import ContentItem from "./ContentItem";

function Content() {
  const dispath = useDispatch()
  const {Movie,MovieDetail} = useSelector((state) => state.movie)
  console.log(Movie)
  useEffect(() => {
    dispath(type.getMoviePage())
  },[dispath])

  const [isModal, setIsModal] = useState(false);
  const [width] = useViewport();
  function handleModal() {
    setIsModal(!isModal);
  }
  return (
    <div
      className="content"
      style={
          width > 1200
          ? { gridTemplateColumns: "repeat(5,auto)" }
          : width > 1000
          ? { gridTemplateColumns: "repeat(4,auto)" }
          : width > 760
          ? { gridTemplateColumns: "repeat(3,auto)" }
          : width > 600
          ? { gridTemplateColumns: "repeat(2,auto)" }
          : { gridTemplateColumns: "repeat(1,auto)" }
      }
    >
      
      {/* {Movie && Movie.map(function(data,index) {
        return <ContentItem key={index} onClick={handleModal} data = {data}/>
      })} */}
      
      <Modal data = {MovieDetail} show={isModal} onClick={handleModal} />
    </div>
  );
}

export default Content;
