import axios from "axios";
import * as types from "../type";

const request = axios.create({
  baseURL: "https://ophim1.com/danh-sach/",
})

export const getMoviePage = () => async (dispath) => {
  try {
    const result = await axios.get(
      `phim-moi-cap-nhat?page=1`
    );
    dispath({
      type: types.GET_MOVIE_PAGE,
      payload: result.data,
    });
  } catch (error) {
    console.log("Get movie API error", error);
  }
};

export const getMovieDetail = (movie) => function(dispath) {
  return dispath({ type: types.GET_MOVIE_DETAIL, payload: movie });
}
