import { useState } from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import * as type from "../../store/action";
import "./Content.scss";
function ContentItem({ onClick, data }) {
  const dispath = useDispatch();
  const {Movie} = useSelector((state) => state.movie)
  const [movie,setMovie] = useState({})
  useEffect(() => {
    // dispath(type.getMovie(data.slug))
    type.request.get(`/phim/${data.slug}`)
    .then(res => setMovie(res.data.movie))
  },[])

  
  console.log(movie)
  return (
    <div
      className="content_item"
      onClick={function () {
        onClick();
      }}
    >
      <img className="content_img" src={Movie && Movie.poster_url} alt="hinh anh" />
      <p className="content_name">{Movie && data.name}</p>
    </div>
  );
}

export default ContentItem;
