import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useViewport } from "../../hook";
import * as type from "../../store/action";
import Modal from "../Modal/Modal";
import "./Content.scss";
import ContentItem from "./ContentItem";

function Content() {
  const dispath = useDispatch();
  const { MoviePage, MovieDetail } = useSelector((state) => state.movie);
  useEffect(() => {
    dispath(type.getMoviePage(5));
  }, [dispath]);

  const [isModal, setIsModal] = useState(false);
  const [movieInfo, setMovieInfo] = useState([]);
  const [urlMovie, setUrlMovie] = useState([]);
  const [width] = useViewport();
  function handleModal() {
    setIsModal(!isModal);
  }

  function getInfoMovie(movie) {
    setMovieInfo([movie]);
  }

  function getUrlMovie(movieUrl) {
    setUrlMovie(movieUrl)
    console.log(movieUrl);
  }
  return (
    <div
      className="content"
      style={
        width > 1200
          ? { gridTemplateColumns: "repeat(5,auto)" }
          : width > 1000
          ? { gridTemplateColumns: "repeat(4,auto)" }
          : width > 760
          ? { gridTemplateColumns: "repeat(3,auto)" }
          : width > 600
          ? { gridTemplateColumns: "repeat(2,auto)" }
          : { gridTemplateColumns: "repeat(1,auto)" }
      }
    >
      {MoviePage &&
        MoviePage.map(function (data, index) {
          return (
            <ContentItem
              movieUrl={getUrlMovie}
              key={index}
              getInfo={getInfoMovie}
              onClick={handleModal}
              data={data}
            />
          );
        })}

      <Modal url = {urlMovie} data={movieInfo} show={isModal} onClick={handleModal} />
    </div>
  );
}

export default Content;
