  }
  return (
    <div
      className="content"
      style={
          width > 1200
          ? { gridTemplateColumns: "repeat(5,auto)" }
          : width > 1000
          ? { gridTemplateColumns: "repeat(4,auto)" }
          : width > 760
          ? { gridTemplateColumns: "repeat(3,auto)" }
          : width > 600
          ? { gridTemplateColumns: "repeat(2,auto)" }
          : { gridTemplateColumns: "repeat(1,auto)" }
      }
    >
      {MoviePage && MoviePage.map(function(data,index) {
        return <ContentItem key={index} getInfo = {getInfoMovie} onClick={handleModal} data = {data}/>
      })}
      
      <Modal data = {movieInfo} show={isModal} onClick={handleModal} />
    </div>
  );
}

export default Content;
