import { useState } from "react";
import "./Content.scss";
import * as type from "../../store/action";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import axios from "axios";
function ContentItem({ onClick, data }) {
  const dispath = useDispatch();
  const {MovieDetail} = useSelector((state) => state.movie)

  type.request.get(`/phim/${data.slug}`)
  .then(res => console.log(res.data.movie) )
  
  return (
    <div
      className="content_item"
      onClick={function () {
        onClick();
      }}
    >
      <img className="content_img" src={data.imageUrl} alt="hinh anh" />
      <p className="content_name">{data.title}</p>
    </div>
  );
}

export default ContentItem;
